import React, { useRef, useState, useEffect } from 'react'
import axios from 'axios'

function ContentData() {
    let date = new Date();

    const headerRef = useRef()
    const seoheaderRef = useRef()
    const tagRef = useRef()
    const infoRef = useRef()
    const contentdataRef = useRef()

    const [content, setContent] = useState([])
    let payload = [];
    function saveFunc() {
        let contentRef = '<p> ' + contentdataRef.current.value + ' </p>';

        if (content.length > 0) {
            payload.push(...content)
        }
        payload.push(contentRef)
        setContent(payload)

        contentdataRef.current.value = ''
    }

    const [payloadTime, setPayloadTime] = useState('');
    function sendFunc(event) {
        let header = headerRef.current.value;
        let seoheader = seoheaderRef.current.value;
        let tag = tagRef.current.value;
        let info = infoRef.current.value;




        if (true) {
            let index = 0;
            var payloadTimeStore;
            for (index; index < content.length; index++) {
                console.log('Dongu ' + content[index].split(" ").length)
                payloadTimeStore = content[index].split(" ").length
            }

            if (payloadTimeStore > 0) {
                if (payloadTimeStore < 100) {
                    setPayloadTime('0.30 Saniye')
                } else if (payloadTimeStore < 500) {
                    setPayloadTime('3 Dakika');
                } else if (payloadTimeStore < 1000) {
                    setPayloadTime('5 Dakika');
                } else if (payloadTimeStore < 1500) {
                    setPayloadTime('8 Dakika');
                } else if (payloadTimeStore < 2000) {
                    setPayloadTime('10 Dakika');
                } else if (payloadTimeStore < 2500) {
                    setPayloadTime('13 Dakika');
                } else if (payloadTimeStore < 3000) {
                    setPayloadTime('15 Dakika');
                } else if (payloadTimeStore < 3500) {
                    setPayloadTime('18 Dakika');
                } else if (payloadTimeStore < 4000) {
                    setPayloadTime('20 Dakika');
                } else if (payloadTimeStore < 4500) {
                    setPayloadTime('23 Dakika');
                } else if (payloadTimeStore < 5000) {
                    setPayloadTime('25 Dakika');
                } else if (payloadTimeStore < 5500) {
                    setPayloadTime('28 Dakika');
                } else {
                    setPayloadTime('30 Dakikadan fazla');
                }
            }
            console.log(payloadTime)
        }











        useEffect(() => {
            let postData = {
                _id: Math.floor(Math.random() * 20) + 10,
                header,
                seoheader,
                tag,
                date: date,
                info,
                content,
                readtime: payloadTime
            };

            const options = {
                url: 'http://192.168.254.251:8558/blog-set-content/',
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json;charset=UTF-8'
                },
                data: postData
            };

            axios(options).then((response) => { alert('Data Basarili bir sekilde gonderildi') });

            console.log(postData)

            headerRef.current.value = ''
            seoheaderRef.current.value = ''
            tagRef.current.value = ''
            contentdataRef.current.value = ''
            infoRef.current.value = ''

        }, [])

    }



    return (
        <>
            <div className="container">
                <form action="" method="post">
                    <label htmlFor="header">Header</label>
                    <input ref={headerRef} type="text" id="header" name="header" placeholder="Header.." />

                    <label htmlFor="seoheader">Seo-Header</label>
                    <input ref={seoheaderRef} type="text" id="seoheader" name="seoheader" placeholder="Header.." />

                    <label htmlFor="tag">Tag</label>
                    <input ref={tagRef} type="text" id="tag" name="tag" placeholder="Tag.." />

                    <div className="contentdata">
                        <label htmlFor="contentdata">Content-Data</label>
                        <textarea ref={contentdataRef} id="contentdata" name="contentdata" placeholder="Write something.."></textarea>
                        <button className='button' onClick={saveFunc} type="button">Save</button>
                    </div>

                    <label htmlFor="contentinfo">Content-info</label>
                    <textarea ref={infoRef} id="contentinfo" name="contentinfo" placeholder="Write something.."></textarea>

                    <button className='button' type="button" onClick={sendFunc}>Send</button>
                </form>
            </div>
        </>
    );
}

export default ContentData;
