import React, { useState, useEffect } from 'react'
import Projectstbox from './projectsbox'

import axios from 'axios'


export default function Contact() {
    const [check, setCheck] = useState(0)


    const [lastData, setLastData] = useState([]);
    async function getLastProjects() {
        await axios.get('http://192.168.254.251:8558/admin-get-projects/')
            .then(function (response) {
                setLastData(response.data.getData)
            })
    }

    useEffect(() => {
        getLastProjects()
        setCheck(0)
    }, [check])

    async function setProject(data) {
        const options = {
            url: 'http://192.168.254.251:8558/admin-set-project/',
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json;charset=UTF-8'
            },
            data: data
        };
        await axios(options).then((response) => { alert('Project Basarili bir sekilde eklendi.') });
        setCheck(1)
    }

    function setProjectFunc(parentId) {
        let data = {
            parentNodeId: String(parentId),
            rule: {
                name: 'readed',
                rule: true
            }
        }
        setProject(data)
    }

    return (
        <>
            {
                (lastData.length > 0) ?
                    lastData.map(item => {
                        return <Projectstbox key={item._id} data={item} setProjectFunc={setProjectFunc} />
                    })
                    :
                    ''
            }
        </>
    )
}
