import React, { useRef } from 'react'

export default function ContentData() {
    // let date = new Date();

    const header = useRef()
    const seoheader = useRef()
    const tag = useRef()
    const info = useRef()
    const contentdata = useRef()

    function Func(event) {
        // let payload = [];
        let header = header.current.value;
        let seoheader = seoheader.current.value;
        let tag = tag.current.value;
        let contentdata = contentdata.current.value;
        let info = info.current.value;

        let userData = {
            _id: Math.floor(Math.random() * 20) + 10,
            header,
            seoheader,
            tag,
            date: new Date(),
            info,
            contentdata
        };
        // if (data.length > 0) {
        //     payload.push(...data)
        // }
        // payload.push(userData);
        // setData(payload)

        // const options = {
        //     url: 'http://192.168.254.251:8558/contact-set-content/',
        //     method: 'POST',
        //     headers: {
        //         'Accept': 'application/json',
        //         'Content-Type': 'application/json;charset=UTF-8'
        //     },
        //     data: userData
        // };

        // axios(options).then((response) => { console.log(response.data.getData) });

        // document.getElementById('fname').value = ''
        // document.getElementById('lname').value = ''
        // document.getElementById('subject-contact').value = ''
        // emailCheckSet(undefined)


        // setNoti(true)
    }




    return (
        <>
            <div class="container">
                <form action="" method="post">
                    <label htmlFor="header">Header</label>
                    <input ref={header} type="text" id="header" name="header" placeholder="Header.." />

                    <label htmlFor="seoheader">Seo-Header</label>
                    <input ref={seoheader} type="text" id="seoheader" name="seoheader" placeholder="Header.." />

                    <label htmlFor="tag">Tag</label>
                    <input ref={tag} type="text" id="tag" name="tag" placeholder="Tag.." />

                    <div className="contentdata">
                        <label htmlFor="contentdata">Content-Data</label>
                        <textarea ref={contentdata} id="contentdata" name="contentdata" placeholder="Write something.."></textarea>
                        <button type="button">Save</button>
                    </div>

                    <label htmlFor="contentinfo">Content-info</label>
                    <textarea ref={info} id="contentinfo" name="contentinfo" placeholder="Write something.."></textarea>

                    <button type="button" onClick={Func}>Send</button>
                </form>
            </div>
        </>
    );
}
