import React, { useState, useRef, useEffect } from 'react'

function Todolist() {
    const contentdataRef = useRef()


    const [todolist, setTodolist] = useState([])
    function saveTodolist() {
        setTodolist(contentdataRef.current.value)
        alert(todolist)
    }



    useEffect(() => {
        console.log(todolist)
    }, [todolist])





    return (
        <>
            <div className="contentdata">
                <h3 style={{ borderBottom: ".3em solid #444", marginBottom: "1em", paddingBottom: ".3em" }}>TodoList Add</h3>
                <textarea ref={contentdataRef} id="contentdata" name="contentdata" placeholder="Write something.."></textarea>
                <button className='button' onClick={saveTodolist} type="button">Save</button>
            </div>
        </>
    )
}
export default Todolist;
