import React, { useEffect, useState } from 'react'
import axios from 'axios'

import Commentbox from './commentbox'

function Comment() {


    async function changeCommentRules(data) {
        const options = {
            url: 'http://192.168.254.251:8558/admin-comment-set-rule-change',
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json;charset=UTF-8'
            },
            data: data
        };
        await axios(options).then((response) => { alert('Comment Basarili bir sekilde degistirildi') });
    }

    const [lastData, setLastData] = useState([]);
    async function getLastComment() {
        await axios.get('http://localhost:8558/admin-comment-get-all')
            .then(function (response) {
                setLastData(response.data.getData)
            })
    }


    const [payload, setPayload] = useState({});
    function ignoreFunc(parentId, childId) {
        let data = {
            parentNodeId: String(parentId),
            childNodeId: String(childId),
            rule: {
                name: 'ignore',
                rule: true
            }
        }
        setPayload(data)
        changeCommentRules(payload)
    }
    function readedFunc(parentId, childId) {
        let data = {
            parentNodeId: String(parentId),
            childNodeId: String(childId),
            rule: {
                name: 'readed',
                rule: true
            }
        }
        setPayload(data)
        changeCommentRules(payload)
    }

    useEffect(() => {
        getLastComment()
    }, [payload])


    return (
        <>

            {
                lastData.map(item => {
                    return item.comment.map(item2 => {
                        if (!item2.didYouRead) {
                            if (!item2.ignore) {
                                return <Commentbox key={item2._id} parentNodeId={item._id} data={item2} ignoreFunc={ignoreFunc} readedFunc={readedFunc} />
                            } else {
                                return ''
                            }
                        } else {
                            return ''
                        }
                    })
                })
            }

        </>
    );
}
export default Comment;
