import React, { useState, useEffect, useRef } from 'react'
import Projectstbox from './projectsbox'

import axios from 'axios'


export default function Contact() {
    const projectNameRef = useRef()
    const projectInfoRef = useRef()
    const projectAddressRef = useRef()


    const [check, setCheck] = useState(0)


    const [lastData, setLastData] = useState([]);
    async function getLastProjects() {
        await axios.get('http://192.168.254.251:8558/admin-get-projects/')
            .then(function (response) {
                setLastData(response.data.getData.getData)
            })
    }

    useEffect(() => {
        getLastProjects()
        setCheck(0)
    }, [check])





    async function setProject(data) {
        let projectName = projectNameRef.current.value;
        let projectInfo = projectNameRef.current.value;
        let projectAddress = projectNameRef.current.value;

        console.log(projectName + projectInfo + projectAddress);

        // const options = {
        //     url: 'http://192.168.254.251:8558/admin-set-project/',
        //     method: 'POST',
        //     headers: {
        //         'Accept': 'application/json',
        //         'Content-Type': 'application/json;charset=UTF-8'
        //     },
        //     data: data
        // };
        // await axios(options).then((response) => { alert('Project Basarili bir sekilde eklendi.') });
        // setCheck(1)
    }

    function setProjectFunc(parentId) {
        let data = {
            parentNodeId: String(parentId),
            rule: {
                name: 'readed',
                rule: true
            }
        }
        setProject(data)
    }

    return (
        <>
            {
                (lastData.length > 0) ?
                    lastData.map(item => {
                        return <Projectstbox key={item._id} data={item} setProjectFunc={setProjectFunc} />
                    })
                    :
                    ''
            }
            <div className="project-add">
                <form action="">
                    <label htmlFor="project">Add Project</label>
                    <input ref={projectNameRef} type="text" id="projectName" name="project" placeholder="Project add.." />
                    <label htmlFor="project">Add Project</label>
                    <input ref={projectInfoRef} type="text" id="projectInfo" name="project" placeholder="Project add.." />
                    <label htmlFor="project">Add Project</label>
                    <input ref={projectAddressRef} type="text" id="projectAddress" name="project" placeholder="Project add.." />
                </form>
                <button type='button' onClick={setProject} >Send</button>

            </div>
            <style jsx>{`












            `}</style>
        </>
    )
}
