import React from 'react'

export default function ContentData() {
    let date = new Date();

    function oneFunc(event) {
        let payload = [];
        let header = document.getElementById('header').value
        let userEmail = document.getElementById('lname').value
        let userContent = document.getElementById('subject-contact').value


        let userData = {
            _id: Math.floor(Math.random() * 20) + 10,
            header: header,
            seoheader: userEmail,
            tag: userContent,
            date: new Date(),
            info: contentinfo,
            content: contentdata
        };
        if (data.length > 0) {
            payload.push(...data)
        }
        payload.push(userData);
        setData(payload)

        const options = {
            url: 'http://192.168.254.251:8558/contact-set-content/',
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json;charset=UTF-8'
            },
            data: userData
        };

        axios(options).then((response) => { console.log(response.data.getData) });

        document.getElementById('fname').value = ''
        document.getElementById('lname').value = ''
        document.getElementById('subject-contact').value = ''
        emailCheckSet(undefined)


        setNoti(true)
    }




    return (
        <>
            <div class="container">
                <form action="" method="post">
                    <label htmlFor="header">Header</label>
                    <input type="text" id="header" name="header" placeholder="Header.." />

                    <label htmlFor="seoheader">Seo-Header</label>
                    <input type="text" id="seoheader" name="seoheader" placeholder="Header.." />

                    <label htmlFor="tag">Tag</label>
                    <input type="text" id="tag" name="tag" placeholder="Tag.." />

                    <div className="contentdata">
                        <label htmlFor="contentdata">Content-Data</label>
                        <textarea id="contentdata" name="contentdata" placeholder="Write something.."></textarea>
                        <button type="button">Save</button>
                    </div>

                    <label htmlFor="contentinfo">Content-info</label>
                    <textarea id="contentinfo" name="contentinfo" placeholder="Write something.."></textarea>

                    <button type="button">Send</button>
                </form>
            </div>
        </>
    );
}
